import React, { useState, useEffect } from 'react';

// Image paths relative to the 'public' folder
const heroImages = Array.from({ length: 10 }, (_, i) => `/images/hero-${i + 1}.jpg`);

const LandingPage = () => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [backgroundStyle, setBackgroundStyle] = useState({});

  // Effect to change the image index every 7 seconds
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % heroImages.length);
    }, 7000); // Change image every 7 seconds

    // Cleanup interval on component unmount
    return () => clearInterval(timer);
  }, []);

  // Effect to update the background style when the index changes
  useEffect(() => {
    setBackgroundStyle({
      backgroundImage: `url('${heroImages[currentImageIndex]}')`,
    });
    // Optional: Preload next image (can be added later for optimization)
    // const nextImageIndex = (currentImageIndex + 1) % heroImages.length;
    // const img = new Image();
    // img.src = heroImages[nextImageIndex];

  }, [currentImageIndex]);

  return (
    <section
      id="home" // Keep the ID if needed for navigation/linking
      className="hero-section min-h-screen flex justify-center items-center text-center text-white relative overflow-hidden bg-cover bg-center bg-no-repeat transition-all duration-1000 ease-in-out"
      style={backgroundStyle} // Apply dynamic background style
    >
      {/* Optional: Add overlay using ::before in CSS or a separate div if needed for text contrast */}
      {/* <div className="absolute inset-0 bg-black opacity-30"></div> */}
      <div className="z-10 p-8">
        <h1 className="text-5xl font-bold mb-4">Experience the Wild</h1>
        <p className="text-xl mb-8">Discover America's natural treasures. Plan your next adventure.</p>
        <a href="#explore" className="bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-6 rounded transition duration-300 ease-in-out">
          Explore Parks
        </a>
      </div>
    </section>
    // TODO: Add other sections (About, Features, Map, Partners, Footer) as components
  );
};

export default LandingPage;
